// Check if a name is valid (not a location, not garbage data, is a proper first name)
function isValidName(name: string): boolean {
  if (!name || name.length < 2) return false
  // Invalid patterns - locations, garbage, generic words
  const invalidPatterns = [
    'Auckland', 'Hamilton', 'Wellington', 'Christchurch', 'Napier', 'Hastings', 'Taupo',
    'Whangarei', 'Palmerston', 'Invercargill', 'Dunedin', 'Rotorua', 'Tauranga', 'Nelson',
    'New Zealand', 'Flat', 'Jump', 'Cars', 'Fault', 'Other', 'Miss', 'Ipl', 'Jg', 'Ly',
    'Ah', 'Sar', 'Pool', 'Zo', 'hanzalAnna', 'SarLuke'
  ]
  const lowerName = name.toLowerCase()
  for (const pattern of invalidPatterns) {
    if (lowerName === pattern.toLowerCase() || name === pattern) return false
  }
  // Check it's not all caps (likely garbage) unless short like 'BEN'
  if (name.length > 4 && name === name.toUpperCase()) return false
  return true
}

// Get a valid name - use customerName if valid, otherwise generate from backupNames
function getValidName(story: Story, index: number): string {
  if (isValidName(story.customerName)) {
    // Capitalize first letter
    return story.customerName.charAt(0).toUpperCase() + story.customerName.slice(1)
  }
  // Use backup name based on index for consistency
  return backupNames[index % backupNames.length]
}

