// Generate a proper story based on the actual data
function generateCorrectStory(story: Story, index: number): string {
  const name = getValidName(story, index)
  const vehicle = story.vehicle
  const location = story.location
  const time = story.responseTime
  
  // Pick template based on issue type
  let templates: ((n: string, v: string, l: string, t: string) => string)[]
  const issueLower = story.issue.toLowerCase()
  
  if (issueLower.includes('battery')) {
    templates = storyTemplates.flatBattery
  } else if (issueLower.includes('tyre') || issueLower.includes('tire')) {
    templates = storyTemplates.flatTyre
  } else if (issueLower.includes('lock')) {
    templates = storyTemplates.lockout
  } else if (issueLower.includes('accident') || issueLower.includes('collision')) {
    templates = storyTemplates.accident
  } else if (issueLower.includes('fuel') || issueLower.includes('petrol') || issueLower.includes('diesel')) {
    templates = storyTemplates.fuel
  } else {
    templates = storyTemplates.breakdown
  }
  
  // Use index to pick a template consistently
  const templateIndex = index % templates.length
  return templates[templateIndex](name, vehicle, location, time)
}

// Get a story with corrected text
function getFixedStory(story: Story, index: number): Story {
  return {
    ...story,
    customerName: getValidName(story, index),
    story: generateCorrectStory(story, index),
  }
}

// Helper functions
export function getStoriesByLocation(slug: string): Story[] {
  return stories
    .map((s, i) => ({ story: s, index: i }))
    .filter(({ story }) => story.slug === slug || story.slug.includes(slug))
    .map(({ story, index }) => getFixedStory(story, index))
}

export function getStoriesByRegion(region: string): Story[] {
  return stories
    .map((s, i) => ({ story: s, index: i }))
    .filter(({ story }) => story.region === region)
    .map(({ story, index }) => getFixedStory(story, index))
}

// Returns ALL stories for endless carousel rotation
export function getFeaturedStories(count?: number): Story[] {
  const allStories = stories.map((s, i) => getFixedStory(s, i))
  // If count is specified and less than total, slice. Otherwise return all.
  if (count && count < allStories.length) {
    return allStories.slice(0, count)
  }
  return allStories
}

// Get all stories with fixed text for endless carousel
export function getAllStories(): Story[] {
  return stories.map((s, i) => getFixedStory(s, i))
}

export function getAllRegions(): string[] {
  return Array.from(new Set(stories.map(s => s.region))).sort((a, b) => {
    if (a === 'Waikato') return -1
    if (b === 'Waikato') return 1
    return a.localeCompare(b)
  })
}

export function getStoryCount(): number {
  return stories.length
}

// Get all unique location slugs
export function getAllLocationSlugs(): string[] {
  return Array.from(new Set(stories.map(s => s.slug))).sort()
}

// Get all unique locations with their slugs and counts
export function getAllLocations(): { slug: string; name: string; region: string; count: number }[] {
  const locationMap = new Map<string, { name: string; region: string; count: number }>()
  
  stories.forEach(s => {
    const existing = locationMap.get(s.slug)
    if (existing) {
      existing.count++
    } else {
      locationMap.set(s.slug, { name: s.location, region: s.region, count: 1 })
    }
  })
  
  return Array.from(locationMap.entries())
    .map(([slug, data]) => ({ slug, ...data }))
    .sort((a, b) => b.count - a.count) // Sort by count descending
}

// Get stories from OTHER locations (for carousel navigation to different areas)
export function getStoriesFromOtherLocations(currentSlug: string, count: number = 50): Story[] {
  const otherStories = stories
    .map((s, i) => ({ story: s, index: i }))
    .filter(({ story }) => story.slug !== currentSlug)
  
  return otherStories.slice(0, count).map(({ story, index }) => getFixedStory(story, index))
}

// Get all unique issue types
export function getAllIssueTypes(): string[] {
  return Array.from(new Set(stories.map(s => s.issue))).sort()
}

// Get stories by location and optionally filter by issue type
export function getStoriesByLocationAndIssue(slug: string, issueType?: string): Story[] {
  let filtered = stories
    .map((s, i) => ({ story: s, index: i }))
    .filter(({ story }) => story.slug === slug)
  
  if (issueType && issueType !== 'all') {
    filtered = filtered.filter(({ story }) => story.issue === issueType)
  }
  
  return filtered.map(({ story, index }) => getFixedStory(story, index))
}

// Get location details by slug
export function getLocationBySlug(slug: string): { name: string; region: string } | null {
  const story = stories.find(s => s.slug === slug)
  if (!story) return null
  return { name: story.location, region: story.region }
}

