// JOB TYPE / SERVICE HELPERS
// ============================================

// Get all unique issue/job types with counts
export function getAllJobTypes(): { type: string; slug: string; count: number }[] {
  const jobMap = new Map<string, number>()
  stories.forEach(s => {
    const count = jobMap.get(s.issue) || 0
    jobMap.set(s.issue, count + 1)
  })
  return Array.from(jobMap.entries())
    .map(([type, count]) => ({
      type,
      slug: type.toLowerCase().replace(/\s+/g, '-'),
      count
    }))
    .sort((a, b) => b.count - a.count)
}

// Get stories by job/issue type
export function getStoriesByJobType(jobSlug: string): Story[] {
  const issueType = jobSlug.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')
  return stories
    .map((s, i) => ({ story: s, index: i }))
    .filter(({ story }) => story.issue.toLowerCase() === issueType.toLowerCase())
    .map(({ story, index }) => getFixedStory(story, index))
}

// Get job type details by slug
export function getJobTypeBySlug(slug: string): { name: string; count: number } | null {
  const issueType = slug.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')
  const matchingStories = stories.filter(s => s.issue.toLowerCase() === issueType.toLowerCase())
  if (matchingStories.length === 0) return null
  return { name: matchingStories[0].issue, count: matchingStories.length }
}

// ============================================
// VEHICLE BRAND HELPERS
// ============================================

function extractBrand(vehicle: string): string {
  const brandMap: Record<string, string> = {
    'vw': 'Volkswagen', 'volkswagen': 'Volkswagen',
    'merc': 'Mercedes', 'mercedes': 'Mercedes', 'mercedes-benz': 'Mercedes',
    'bmw': 'BMW', 'land': 'Land Rover', 'range': 'Range Rover',
  }
  const firstWord = vehicle.split(' ')[0].toLowerCase()
  return brandMap[firstWord] || vehicle.split(' ')[0]
}

// Get all unique vehicle brands with counts
export function getAllVehicleBrands(): { brand: string; slug: string; count: number }[] {
  const brandMap = new Map<string, number>()
  stories.forEach(s => {
    const brand = extractBrand(s.vehicle)
    const normalizedBrand = brand.charAt(0).toUpperCase() + brand.slice(1).toLowerCase()
    const count = brandMap.get(normalizedBrand) || 0
    brandMap.set(normalizedBrand, count + 1)
  })
  return Array.from(brandMap.entries())
    .map(([brand, count]) => ({ brand, slug: brand.toLowerCase().replace(/\s+/g, '-'), count }))
    .filter(b => b.count >= 2)
    .sort((a, b) => b.count - a.count)
}

// Get stories by vehicle brand
export function getStoriesByBrand(brandSlug: string): Story[] {
  const brandName = brandSlug.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')
  return stories
    .map((s, i) => ({ story: s, index: i }))
    .filter(({ story }) => {
      const storyBrand = extractBrand(story.vehicle)
      return storyBrand.toLowerCase() === brandName.toLowerCase()
    })
    .map(({ story, index }) => getFixedStory(story, index))
}

// Get brand details by slug
export function getBrandBySlug(slug: string): { name: string; count: number } | null {
  const brandName = slug.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')
  const matchingStories = stories.filter(s => {
    const storyBrand = extractBrand(s.vehicle)
    return storyBrand.toLowerCase() === brandName.toLowerCase()
  })
  if (matchingStories.length === 0) return null
  return { name: brandName, count: matchingStories.length }
}